public class FindTheDifference {
    public static void main(String[] args) {
        System.out.println(new FindTheDifference().findTheDifference("", ""));
    }

    public char findTheDifference(String s, String t) {
        if (s.length() != t.length() - 1) {
            return ' ';
        }
        int i = 0;
        int j = 0;
        int sum = 0;
        while (j < t.length()) {
            sum += (t.charAt(j) - 'a');
            if (i < s.length()) {
                sum -= (s.charAt(i) - 'a');
            }
            ++i;
            ++j;
        }
        return (char) (sum + 'a');
    }
}
